  onNotFound,
  onError
}) => {
  models.usuarios.findOne({
    where: {
      id
    }
  }).then(usuarios => usuarios ? onSuccess(usuarios) : onNotFound()).catch(() => onError());
};

const deleteUsuario = async (req, res) => {
  const onSuccess = postulaciones => postulaciones.destroy().then(() => res.sendStatus(200)).catch(() => res.sendStatus(500));

  findUsuarioPorId(req.params.id, {
    onSuccess,
    onNotFound: () => res.sendStatus(404),
    onError: () => res.sendStatus(500)
  });
};

exports.deleteUsuario = deleteUsuario;
