  }, {
    fields: ["fk_id_usuario", "fk_id_rubro", "fk_id_estado", "nombre_empresa", "descripcion", "pais", "fk_id_provincia", "descripcion", "fk_id_ciudad", "calle", "nro", "piso", "depto", "cp", "telefono", "web", "nombre_representante", "email_representante"]
