      res.sendStatus(500);
    }
  });

  findEmpresas(req.params.id, {
    onSuccess,
    onNotFound: () => res.sendStatus(404),
    onError: () => res.sendStatus(500)
  });
}; //Con esto cambiamos el estado de una empresa desde administrador.


exports.updateEmpresa = updateEmpresa;

const patchEmpresa = async (req, res) => {
  const onSuccess = empresas => empresas.update({
    fk_id_estado: 1
  }, {
    fields: ["fk_id_estado"]
  }).then(() => res.sendStatus(200), changeGroup(empresas.fk_id_usuario), console.log(empresas.fk_id_usuario)).catch(error => {
    res.status(400);
  });

  findEmpresas(req.params.id, {
