      postulantes ? onSuccess(postulantes) : onNotFound()
    )
    .catch(() => onError());
};

export const getPorId = async (req, res) => {
  findPostulantesPorDNI(req.params.id, {
    onSuccess: (postulantes) => res.send(postulantes),
