export const deletePostulante = async (req, res) => {
  const onSuccess = postulantes =>
  postulantes
      .destroy()
      .then(() => res.sendStatus(200))
      .catch(() => res.sendStatus(500));
    findPostulantesPorDNI(req.params.id, {
