  let ordenarPor = req.query.ordenar;
  let buscarTitulo = req.query.buscarTitulo;
  let idEstado = req.query.idEstado;

  let pagina = 0;
  if (!Number.isNaN(paginaComoNumero) && paginaComoNumero > 0) {
