  } else {
    buscarTitulo = buscarTitulo.replace(/\s/g, "%");
  }

  if (typeof idEstado === "undefined") {
    idEstado = 1;
 }

  models.ofertas
    .findAndCountAll({
      limit: limite,
      offset: pagina * limite,
      include: [
        {
          as: "Empresa",
          model: models.empresas,
          attributes: ["id", "nombre_empresa", "descripcion"],
        },
        {
          as: "Estudio",
          model: models.estudios,
