        },
        {
          as: "Estado",
          model: models.estado_ofertas,
          attributes: ["id", "nombre_estado"],
        },
      ],
      where: {
        [Op.and]: [
          {
            titulo_oferta: {
              [Op.iLike]: `%${buscarTitulo}%`,
            },
            fk_id_estado: {
              [Op.eq]: [idEstado]
            },
          },
        ],
      },
      order: [[ordenarPor, 'DESC'],],
    })
    .then((ofertas) =>
      res.send({
        ofertas,
        totalPaginas: Math.ceil(ofertas.count / limite),
      })
    )
    .catch(() => res.sendStatus(500));
};

