        },
      ],
      where: { id },
    })
    .then((empresas) => (empresas ? onSuccess(empresas) : onNotFound()))
    .catch(() => onError());
};

export const getPorId = async (req, res) => {
  findEmpresas(req.params.id, {
    onSuccess: (empresas) => res.send(empresas),
