  const onSuccess = empresas =>
  empresas
      .destroy()
      .then(() => res.sendStatus(200))
      .catch(() => res.sendStatus(500));
  findEmpresas(req.params.id, {
    onSuccess,
    onNotFound: () => res.sendStatus(404),
