      {
        as: "Oferta",
        model: models.ofertas,
        attributes: ["id", "titulo_oferta"],
      },        
      {
        as: "Empresa",
        model: models.empresas,
        attributes: ["id", "nombre_empresa"],
      },
    ],
    }).then(postulaciones => res.send({
      postulaciones
      
    })).catch(() => res.sendStatus(500));
};

